Example:
{
"1931": 
   {
     "Titles": ["Movie A", "Movie B"],
     "Summaries": ["Movie A revolves around a detective's quest.", "Movie B portrays a family's struggle."]
   },
"1932": 
   {
     "Titles": ["Movie C"],
     "Summaries": ["Movie C showcases a romantic love story."]
   }
...
}

