E = A - B + C;
F = A + B - C;
H = orthocenter(A,B,C);
O = circumcenter(A,B,C);
P = 2*O - H;

draw(A--B--C--cycle);
draw(B--D--E--A);
draw(interp(P,D,-0.2)--interp(P,D,1.2),dashed);
draw(interp(P,E,-0.2)--interp(P,E,1.2),dashed);

label("$A$", A, N);
label("$B$", B, SW);
label("$C$", C, SE);
label("$D$", D, W);
label("$E$", E, SE);
dot("$P$", P, NW);
